package org.obiba.onyx.jade.instrument.summitdoppler;

import java.io.File;
import java.io.IOException;
import java.net.URISyntaxException;
import java.text.DateFormat;
import java.text.SimpleDateFormat;

import org.junit.Test;
import org.obiba.onyx.jade.instrument.summitdoppler.VantageReportParser.ExamData;
import org.obiba.onyx.jade.instrument.summitdoppler.VantageReportParser.SideData;

import static org.hamcrest.core.Is.is;
import static org.hamcrest.core.IsNull.notNullValue;
import static org.junit.Assert.assertThat;

/**
 *
 */
public class VantageReportParserTest {

  private static final DateFormat DATE_FORMAT = new SimpleDateFormat("yyyy-MM-dd - HH:mm:ss");

  @Test
  public void testParse() throws URISyntaxException, IOException {
    VantageReportParser parser = new VantageReportParser();

    File file = new File(getClass().getResource("/VAN00303.ABI").toURI());

    parser.parse(file);

    assertThat(parser.getExamCount(), is(1));
    ExamData exam = parser.getExamData(0);
    assertThat(exam, notNullValue());

    assertThat(exam.getName(), is("TEST"));
    assertThat(DATE_FORMAT.format(exam.getTimestamp()), is("2011-07-14 - 11:52:15"));

    SideData left = exam.getLeft();
    assertThat(left, notNullValue());
    assertThat(left.getBrachial(), is(120));
    assertThat(left.getAnkle(), is(135));
    assertThat((int) (left.getIndex() * 100), is(109));
    assertThat(DATE_FORMAT.format(left.getClock()), is("2011-07-14 - 11:51:16"));
    assertThat(left.toString(left.getWaveForm()),
        is(""));
    assertThat(left.getScale(), is("x8"));

    SideData right = exam.getRight();
    assertThat(right, notNullValue());
    assertThat(right.getBrachial(), is(123));
    assertThat(right.getAnkle(), is(130));
    assertThat((int) (right.getIndex() * 100), is(105));
    assertThat(DATE_FORMAT.format(right.getClock()), is("2011-07-14 - 11:52:15"));

    assertThat(right.toString(right.getWaveForm()),
        is(""));
    assertThat(right.getScale(), is("x8"));
  }

}
